mapper.insert:
INSERT INTO washer_manage.washer (storied_id, name, status, insert_user, update_user)
VALUES (#{washer.storiedId}, #{washer.name}, #{washer.status},
        #{washer.insertUser}, #{washer.updateUser});

mapper.select:
SELECT washer.id, storied_id, name, status, washer.insert_user, washer.insert_time, washer.update_user, washer.update_time, washer.is_deleted, storied.id s_id, number s_number, storied.insert_user s_insert_user, storied.insert_time insert_time, storied.update_user s_update_user, storied.update_time s_update_time, storied.is_deleted s_is_deleted FROM washer_manage.washer, washer_manage.storied
washer.storied_id = storied.id AND washer.is_deleted = 0
and status = #{washer.status}
and storied_id = #{washer.storiedId}
LIMIT #{startIndex}, #{count}
